import React, { Component } from 'react';
import logo from './logo.png';
//import thumb from './thumb.jpg';
import 'bootstrap/dist/css/bootstrap.min.css';
import './App.css';
import { Card, CardImg, CardText, CardBody,
  CardTitle} from 'reactstrap';
import { Button, Popover, PopoverHeader, PopoverBody } from 'reactstrap';
import { Badge } from 'reactstrap';
import { library } from '@fortawesome/fontawesome-svg-core'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faHeart } from '@fortawesome/free-solid-svg-icons'


class App extends Component {
  constructor(props) {
    super(props);
    this.handleClickLikeOn = this.handleClickLikeOn.bind(this);
    this.handleClickLikeOff = this.handleClickLikeOff.bind(this);
    this.toggle = this.toggle.bind(this);

    this.state = {
      popoverOpen: false,
      etat: false,
      // ​viewOnlyLike: false
    };
  }

  toggle() {
    this.setState({
      popoverOpen: !this.state.popoverOpen
    });
  }

  handleClickLikeOn(){
    this.setState({
      etat: true
      // viewOnlyLike: true
    });
  }

  handleClickLikeOff(){
    this.setState({
      etat: false
      // viewOnlyLike: false
    });
  }

  render() {
    console.log(this.state.etat);
    var dataFilm=[{name:"Django", img: "thumb.jpg", description: "blablabli"},
                  {name:"Djangoa", img: "thumb.jpg", description: "blablabli"},
                  {name:"Djangod", img: "thumb.jpg", description: "blablabli"},
                  {name:"Djangos", img: "thumb.jpg", description: "blablabli"},
                  {name:"Djangos", img: "thumb.jpg", description: "blablabli"},
                  {name:"Django", img: "thumb.jpg", description: "blablabli"},
                  {name:"Django", img: "thumb.jpg", description: "blablabli"},
     ]


    var filmList = dataFilm.map(function(film, i){
    return  <Film name={film.name} img={film.img} description={film.description}/>
    })

    var mooviesLast = [];
    var moviesNameListe = [];
    var mooviesCount = 0 ;
    for(var i = 0; i < dataFilm.length; i++){
      moviesNameListe.push(dataFilm[i].name);
      mooviesCount = i + 1;
    }


    for(var i = 0; i < moviesNameListe.length; i++){
      if(i > moviesNameListe.length - 4){
        mooviesLast.push(moviesNameListe[i])
      }
    }


    return (
      <div className="container-fluid">
        <div className="row">
          <div className="header col-12">
            <div className="row">
              <div className="col-1">
                <img src={logo} />
              </div>
              <div className="col-1">
                <a onClick={this.handleClickLikeOff} href="#">Last release</a>
              </div>
              <div className="col-1">
                <a onClick={this.handleClickLikeOn} href="#">My moovies</a>
              </div>
              <div>
                <Button id="Popover1" onClick={this.toggle}>
                {this.props.totalLike}
                </Button>
                <Popover placement="bottom" isOpen={this.state.popoverOpen} target="Popover1" toggle={this.toggle}>
                <PopoverHeader>Derniers films ajoutés</PopoverHeader>
                <PopoverBody>Sed posuere consectetur est at lobortis. Aenean eu leo quam. Pellentesque ornare sem lacinia quam venenatis vestibulum.</PopoverBody>
                </Popover>
                </div>
            </div>
          </div>
          <div className="filmContainer col-12">
            <div className="row">
              {filmList}
            </div>
          </div>
        </div>
      </div>
    );
  }
}



class Film extends Component {
  constructor(){
    super();
    this.clickLike=this.clickLike.bind(this)
    this.state={
      like:"off"
    }
}

clickLike(){
  if (this.state.like == "off") {
    this.setState({
      like: "on"
    })
  } else if(this.state.like == "on"){
    this.setState({
      like: "off"
    })
  }
}

  render() {

    return (
      <div className="col-3">
        <Card>
          <CardImg top width="100%" src={this.props.img} alt="Card image cap" />
          <CardBody className="moovieCard">
            <FontAwesomeIcon onClick={this.clickLike} icon={ faHeart } className={this.state.like} />
            <CardTitle>{this.props.name}</CardTitle>
            <CardText>{this.props.description}</CardText>
          </CardBody>
        </Card>
      </div>
    );
  }
}

export default App;
