import React, { Component } from 'react';
import logo from './logo.png';
import thumb from './thumb.jpg';
import 'bootstrap/dist/css/bootstrap.min.css';
import './App.css';
import { Card, CardImg, CardText, CardBody,
  CardTitle} from 'reactstrap';

import { Badge } from 'reactstrap';

class App extends Component {
  render() {
    var filmList = []
    for(var i = 0; i<40; i++){
      filmList.push(<Film />)
    }
    return (
      <div className="container-fluid">
        <div className="row">
          <Header />
          <div className="filmContainer col-12">
            <div className="row">
              {filmList}
            </div>
          </div>
        </div>
      </div>
    );
  }
}


class Header extends Component {
  render() {

    return (
        <div className="header col-12">
          <div className="row">
            <div className="col-1">
              <img src={logo} />
            </div>
            <div className="col-1">
              <a href="#">Last release</a>
            </div>
            <div className="col-1">
              <a href="#">My moovies</a>
            </div>
            <span className="col-1"><Badge color="secondary">12 Films</Badge></span>
          </div>
        </div>
    );
  }
}

class Film extends Component {
  render() {

    return (
      <div className="col-3">
        <Card>
          <CardImg top width="100%" src={thumb} alt="Card image cap" />
          <CardBody className="moovieCard">
            <CardTitle>Card title</CardTitle>
            <CardText>Some quick example text to build on the card title and make up the bulk of the cards content.</CardText>
          </CardBody>
        </Card>
      </div>
    );
  }
}

export default App;
